                //                     placeholder="first name" 
                //                     onChange={(e)=> this.setState({firstName:e.target.value})}/>
                //                 <input
                //                     type="text"
                //                     placeholder="last name" 
                //                     onChange={(e)=> this.setState({lastName:e.target.value})}/>
                //             </div>
                //         }
                //     </li>
                //     {/*Delete and edit buttons if the edit state is false*/}
                //     {this.state.edit === false &&
                //         <div>
                //             <button
                //                 className="eliminar"
                //                 onClick={() => this.props.consumeApiDelete(users.id)}>Eliminar</button>
                //             <button
                //                 className="actualizar"
                //                 onClick={() => this.setEdit()}>Actualizar</button>
                //         </div>
                //     }
                //     {/*Save and cancel if the edit state is true*/}
                //     {this.state.edit &&
                //         <div>
                //             <button
                //                 className=""
                //                 onClick={() => this.updateData(
                //                     users.id,
                //                     this.state.firstName,
                //                     this.state.lastName
                //                 )}
                //                 disabled={this.state.firstName.length === 0 || this.state.lastName.length === 0}>Save</button>
                //             <button
                //                 className=""
                //                 onClick={() => this.setState({edit: false})}>Cancel</button>
                //         </div>
                //     }

                // </div> */}
            )
        })
    }

    render() {
        return (
            <div className="box">
                <div className="row">
                    <div className="form">
                        <h2>First name:</h2>
                        <input
                            type="text"
                            value={this.state.firstName}
                            placeholder="first name"
                            onChange={(e) => this.setState({ firstName: e.target.value })} />
                        <h2>Last name:</h2>
                        <input
                            type="text"
                            value={this.state.lastName}
                            placeholder="last name"
                            onChange={(e) => this.setState({ lastName: e.target.value })} />
                        <br />
                        <button
                            className="save"
                            onClick={() => this.saveData(this.state.firstName, this.state.lastName)}
                            disabled={this.state.firstName.length === 0 || this.state.lastName.length === 0}>Guardar</button>
                    </div>
                    <div className="list">
                        <ul className="list">
                            {this.createlist()}
                        </ul>
                    </div>


                </div>

            </div >
        );
    }
}

function mapStateToProps({ consumeApiReducer }) {
    return { consumeApiReducer };
}
function mapDispatchToProps(dispatch) {
    return bindActionCreators({
        consumeApi,
        consumeApiPut,
        consumeApiGet,
        consumeApiDelete
    }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(ConsumeApi);
