import axios from 'axios';
const apiRoute = 'http://localhost:7000';

export function consumeApi() {
    return (dispatch) => {
        return axios.post(`${apiRoute}`)
            .then(response => {
                dispatch({
                    type: 'CONSUME_API_POST',
                    payload: response.data
                })
            })
            .catch(error => {
                throw (error);
            });
    }
}

export function consumeApiPut() {
    return (dispatch) => {
        return axios.put(`${apiRoute}/user`)
            .then(response => {
                dispatch({
                    type: 'CONSUME_API_PUT',
                    payload: response.data
                })
            })
            .catch(error => {
                throw (error);
            });
    }
}
