import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { consumeApi, consumeApiPut, consumeApiGet } from '../actions';
import '../App.css';

class ConsumeApi extends Component {
    constructor(props) {
        super(props)
        this.state = {
            firstName: '',
            lastName: ''
        }
    }
    createlist() {

        return this.props.consumeApiReducer.map((users) => {
            return (
                <div>
                    <li key={users.id}>{users.firstName} {users.lastName}</li>
                    <button>Eliminar</button>
                    <button>Actualizar</button>
                </div>
            )
        })

    }
    componentDidMount() {
        this.props.consumeApiGet();
    }
    // componentDidUpdate(prevProps){
    //     if(this.props.consumeApiReducer !== prevProps.consumeApiReducer){
    //         this.props.consumeApiReducer;
    //     }
    // }
    render() {
        return (
            <div className="box">
                <div className="row">
                    <div className="form">
                        <h2>First name:</h2>
                        <input
                            type="text"
                            value={this.state.firstName}
                            placeholder="first name"
                            onChange ={(e)=> this.setState({firstName:e.target.value}) } />
                        <h2>Last name:</h2>
                        <input 
                            type="text"
                            value={this.state.lastName}
                            placeholder="last name"
                            onChange ={(e)=> this.setState({lastName:e.target.value})} />
                        <br />
                        <button 
                            className="save"
                            onClick={()=>this.props.consumeApi(this.state.firstName, this.state.lastName)}>Guardar</button>
                    </div>
                    <div className="list">
                        <ul className="list">
                            {this.createlist()}
                        </ul>
                    </div>


                </div>

            </div >
        );
    }
}

function mapStateToProps({ consumeApiReducer }) {
    return { consumeApiReducer };
}
function mapDispatchToProps(dispatch) {
    return bindActionCreators({ consumeApi, consumeApiPut, consumeApiGet }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(ConsumeApi);
