import axios from 'axios';

export function gitProfile(username){
    return dispatch => {
        return axios.get(`https://api.github.com/users/${username}`)
          .then(response => {
            dispatch({
              type: "GET_GITHUB_INFO",
              payload: response
            })
          })
          .catch(error => {
            throw(error);
          });
    }
}
